 */
export const sortOffersByPriority = (offers: OfferItem[]): OfferItem[] => {
	return [...offers].sort((a, b) => {
		const priorityA = a.priority || 0;
		const priorityB = b.priority || 0;
		return priorityB - priorityA; // Higher priority first
	});
};

