 */
export const getNextSlideIndex = (
	currentIndex: number,
	totalSlides: number,
): number => {
	return (currentIndex + 1) % totalSlides;
};

/**
 * Get previous slide index
 */
export const getPrevSlideIndex = (
	currentIndex: number,
	totalSlides: number,
): number => {
	return currentIndex === 0 ? totalSlides - 1 : currentIndex - 1;
};

