query GetUserDevices($userId: ID!) {
  userDevices(userId: $userId) {
    id
    name
    type
    browser
    os
    lastActive
    location
    trusted
    current
  }
}

query GetNotificationPreferences($userId: ID!) {
  notificationPreferences(userId: $userId) {
    email {
      orders
      promotions
      security
      newsletter
    }
    sms {
      orders
      security
      promotions
    }
    push {
      orders
      promotions
      reminders
    }
  }
}

query OdooGetUserPreferences($userId: ID!) {
  odooUserPreferences(userId: $userId) {
    id
    language
    currency
    timezone
    notifications {
      email
      sms
      push
      marketing
    }
    privacy {
      dataSharing
      marketing
      analytics
      profileVisibility
    }
    security {
      twoFactorEnabled
      sessionTimeout
      deviceManagement
      loginNotifications
    }
    display {
      theme
      density
      animations
    }
    shipping {
      preferredMethod
      autoSelectFastest
      signatureRequired
      weekendDelivery
    }
    communications {
      emailFrequency
      smsEnabled
      pushEnabled
      notificationTiming
    }
  }
}

